<?php

namespace Database\Seeders;

use App\Models\Question;
use Illuminate\Database\Seeder;

class UpdateQuestionTranslationsSeeder extends Seeder
{
    public function run(): void
    {
        $translations = $this->getTranslations();

        foreach ($translations as $translation) {
            Question::where('text', $translation['text_es'])
                ->update([
                    'text_ca' => $translation['text_ca'],
                    'text_eu' => $translation['text_eu'],
                    'text_gl' => $translation['text_gl'],
                    'explanation_ca' => $translation['explanation_ca'],
                    'explanation_eu' => $translation['explanation_eu'],
                    'explanation_gl' => $translation['explanation_gl'],
                    'explanation_simple_ca' => $translation['explanation_simple_ca'],
                    'explanation_simple_eu' => $translation['explanation_simple_eu'],
                    'explanation_simple_gl' => $translation['explanation_simple_gl'],
                ]);
        }

        $this->command->info('✅ Traducciones de preguntas actualizadas correctamente.');
    }

